package com.smartwater.edm.entity;import java.io.Serializable;/** * 类描述   :  * 创建人	：system * 创建时间 ：2018-09-03 10:03:58 * @version 1.0 */public class Alarm implements Serializable {	/**	 */	private static final long serialVersionUID = -884960038424359573L;		/**  */	private int id;	/**  */	private String alarm;	 	/** 	 * 获取 主键	 */		public int getId() {		return id;	}	/** 	 * 设置 主键	 */	public void setId(int id) {		this.id = id;	}	 	/** 	 * 获取 	 */		public String getAlarm() {		return alarm;	}	/** 	 * 设置 	 */	public void setAlarm(String alarm) {		this.alarm = alarm;	}	@Override	public String toString() {		return "Alarm [id=" + id + ", alarm=" + alarm + "]";	}	}